// funci√≥n para simular un fragmento de Never Gonna Give You Up
void Rick :: roll () {
    for (int i = 0, j = 0, k = 0, increment = 1; j < 6; i+=increment, k++) {
        system("cls");
        if (i > 27 || i < 0) {
            increment *= -1;
            i+=increment;
            j++;
        }
        cout << ascii[i] << endl;
        cout << lyrics.substr(0, (k > 162 ? 162 : k));
        this_thread::sleep_for(20ms);
    }
    cout << endl << "[Enter para continuar]" << endl;
    getchar();
    system("cls");
}

// revisar si se trata del c√≥digo konami, expresado en caracteres simples
bool Rick :: isPasscode (string &p) {
    if (p.substr(0, 2) == "^^" && (p.substr(2, 2) == "VV" || p.substr(2, 2) == "vv") && p.substr(4, 4) == "<><>" && (p.substr(8, 2) == "ba" || p.substr(8, 2) == "BA")) {
        return true;
    }
    return false;
}